mapper.delete:
UPDATE ADDFILE
   SET DOC_ID = #{doc_id}
     , DOC_REV_ID = #{doc_rev_id}
     , DATASET_PUID = #{dataset_puid}
     , FILE_NM = #{file_nm}
     , FILE_LOCATION = #{file_location}
     , FILE_TYPE = #{file_type}
 WHERE DOC_ID = #{doc_id}

mapper.select:
SELECT NVL(MAX(ADDFILE_SEQ), 0) + 1 AS ADDFILE_SEQ
  FROM ADDFILE
 WHERE ADDFILE_PUID = #{addfile_puid}
