UPDATE IF_ECO_INFO_FROM_VPM                      
SET


            USER_SKIP = #{USER_SKIP, jdbcType=VARCHAR}


            USER_SKIP = NULL


WHERE 
        GUID = #{GUID, jdbcType=VARCHAR}

mapper.select:
SELECT IS_VALID FROM IF_ECO_INFO_FROM_VPM 
WHERE
    ECO_NO = #{ECO_NO, jdbcType=VARCHAR}
    AND NUM = #{NUM, jdbcType=VARCHAR}
    AND SEQ = #{SEQ, jdbcType=VARCHAR}
    AND IF_DATE = #{IF_DATE, jdbcType=TIMESTAMP}
    AND ROWNUM = 1

mapper.update:

UPDATE IF_ECO_INFO_FROM_VPM                      
SET 
    IF_STATUS = #{IF_STATUS, jdbcType=VARCHAR},
    IF_ERROR_MSG = #{IF_ERROR_MSG, jdbcType=VARCHAR}                           
WHERE 
        ECO_NO = #{ECO_NO, jdbcType=VARCHAR}
        AND NUM = #{NUM, jdbcType=VARCHAR}
        AND SEQ = #{SEQ, jdbcType=VARCHAR}
        AND IF_DATE = #{IF_DATE, jdbcType=TIMESTAMP}

mapper.select:
SELECT * FROM ( SELECT * FROM ( SELECT DECODE(VEH.NON_EPL_CNT, 0, 'N', 'Y') NON_EPL, VEH.* FROM ( SELECT A.IF_DATE || '|' || A.ITEM_ID || '|' || A.REVISION_ID GROUP_KEY, ( SELECT COUNT(*) FROM IF_FILE WHERE PART_NO = A.ITEM_ID AND REVISION_ID = A.REVISION_ID AND IF_DATE = A.IF_DATE AND NON_EPL = 'Y' ) NON_EPL_CNT, A.ITEM_ID PART_NO, get_display_part_no2(A.s7_part_type, A.ITEM_ID) s7_display_part_no , A.S7_PROJECT_CODE, A.S7_PART_TYPE, A.ITEM_ID, A.S7_MATURITY, A.NAME , A.S7_KOR_NAME, A.S7_MAIN_NAME, A.S7_SUB_NAME, A.DESCRIPTION , A.S7_LOC1_FR, A.S7_LOC2_IO, A.S7_LOC3_UL, A.S7_LOC4_EE, A.S7_LOC5_LR , A.S7_REFERENCE, A.S7_UNIT, A.S7_ECO_NO, A.S7_COLOR, A.S7_COLOR_ID , A.S7_REGULATION, A.S7_MATTERIAL, A.S7_ALT_MATERIAL, A.S7_THICKNESS , A.S7_FINISH, A.S7_DRW_STAT, A.S7_SHOW_PART_NO, A.S7_EST_WEIGHT , A.S7_CAL_WEIGHT, A.S7_ACT_WEIGHT, A.S7_CAL_SURFACE, A.S7_BOUNDINGBOX , A.S7_DVP_RESULT, A.S7_RESPONSIBILITY, A.S7_STAGE, A.S7_REGULAR_PART , A.S7_AS_END_ITEM, A.S7_SELECTIVE_PART, A.S7_CHANGE_DESCRIPTION , A.S7_DRW_SIZE, A.S7_SYSTEM_CODE, A.S7_SUB_SYSTEM_CODE , A.REVISION_ID, A.PSTATUS, A.PLAST_MOD_DATE, A.RLAST_MOD_USER , A.ROWING_GROUP, A.ROWNIG_USER, A.PCREATION_DATE, A.V_CREATOR , A.S7_ALT_THICKNESS, A.MIG_FLAG, A.MIG_SEQ, A.S7_CAT_V4_TYPE , A.IF_DATE, A.IS_VALID, A.IF_STATUS, A.IF_ERROR_MSG, A.IF_VPM_ECO_NO , A.IF_VPM_NUM, A.IF_VPM_SEQ, A.IF_VPM_CHANGE_TYPE, A.VPM_ECO FROM IF_VEHPART A WHERE A.IF_DATE BETWEEN TO_DATE(#{FROM_DATE, jdbcType=VARCHAR} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') AND TO_DATE(#{TO_DATE, jdbcType=VARCHAR} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
AND A.ITEM_ID LIKE #{ITEM_ID, jdbcType=VARCHAR}
AND A.IS_VALID = #{IS_VALID, jdbcType=VARCHAR}
AND A.IF_STATUS <> 'I'
AND A.IF_STATUS = #{IF_STATUS, jdbcType=VARCHAR}
) VEH )
WHERE NON_EPL = #{NON_EPL, jdbcType=VARCHAR}
) ORDER BY IF_DATE, ITEM_ID, REVISION_ID, PLAST_MOD_DATE

SELECT IF_STATUS FROM 
    IF_VEHPART WHERE
        ITEM_ID = #{ITEM_ID, jdbcType=VARCHAR}

            AND REVISION_ID = #{REVISION_ID, jdbcType=VARCHAR}           

        AND IF_DATE <= #{IF_DATE, jdbcType=TIMESTAMP}

mapper.update:

UPDATE IF_VEHPART                      
SET             
    IF_STATUS = #{IF_STATUS, jdbcType=VARCHAR},
    IF_ERROR_MSG = #{IF_ERROR_MSG, jdbcType=VARCHAR}
WHERE 
        ITEM_ID = #{ITEM_ID, jdbcType=VARCHAR}
        AND REVISION_ID = #{REVISION_ID, jdbcType=VARCHAR}                
        AND IF_DATE = #{IF_DATE, jdbcType=TIMESTAMP}


