mapper.select:
SELECT PROJECT_NO AS PROJECT, SPEC_NO AS "SPEC", WIRING_TYPE AS "WIRING TYPE", SUBSTR(XMLAGG(XMLELEMENT("OP", ' AND ', OP) ORDER BY OP).EXTRACT('//text()').GETSTRINGVAL(), LENGTH(' AND ')+1) AS "STANDARD OPTION" FROM ( SELECT M.PROJECT_NO, M.SPEC_NO, WIRING_TYPE, D.CATE_NO || D.OPTION_NO OP FROM HBOM.PSPEC_MASTER M, HBOM.PSPEC_DETAIL D, WIRING_CATEGORY_NO WOC WHERE M.SPEC_NO = D.SPEC_NO AND M.PROJECT_NO = D.PROJECT_NO AND D.ITNO IS NOT NULL AND D.PROJECT_NO = WOC.PROJECT_CODE AND D.CATE_NO = WOC.CATEGORY_NO AND M.USE_CD = 'E' --수출 AND M.STATUS = 'CURR' --운영스펙 AND M.SPEC_NO = #{SPEC_NO, jdbcType=VARCHAR}
AND WOC.WIRING_TYPE = #{WIRING_TYPE, jdbcType=VARCHAR}
) GROUP BY PROJECT_NO, SPEC_NO, WIRING_TYPE ORDER BY 1,2,3
